public class DB3 {

    /**
     * @param args the command line arguments
     */
    public static void main(String[] args) {

        // TODO Auto-generated method stub
    int value1 = 1;
    int value2 = 2;
    int value3 = 0;


    if(value1 == value2)
        System.out.println("value1 equals value2");
        

    if (value1 != value2) 
        System.out.println("value1 not equal to value2");
        

    if(value1 > value2) 
        System.out.println("value1 greater than value2");
        

    if(value1 > value2) 
        System.out.println("value1 less than value2");

    System.out.println("value1 less than or equal to value2");


    value3 = value1 + value2;
    value3 = value3 - value1;
        

    System.out.println("value1: " + value1);
    System.out.println("value2: " + value2);
    System.out.println("value3: " + value3);
   
    }
}
    
